import {Component} from 'react';
import{Link} from 'react-router-dom';
import {parseJwt} from '../../services/auth';

import Header from '../../Components/header/header'
import '../../App.css';

class Admin extends Component{
    constructor(props){
        super(props);
        this.state = {
            listaConsultas : [],
            medicoNome : '',
            pacienteNome : '',
            horarioConsulta : '',
            statusConsulta : [],
            descricaoDaConsulta : '',


        }
    }

    limparCampos = () => {
        this.setState({
            descricaoConsulta : '',
            statusConsulta : [],
            horarioConsulta : '',
            pacienteNome : '',
            medicoNome : ''
        })
    }

    /* BUSCAR CONSULTAS  */
    buscarConsultas = () => {

        console.log("Vamos realizar a chamada para a API")

        fetch('http://localhost:5000/api/consulta/minhas-consultas/' + parseJwt().jti, {
            headers : {
                'Authorization' : 'Bearer ' + localStorage.getItem('usuario-login')
            }
        })

        .then(resposta => resposta.json())

        .then(data => this.setState({listaConsultas : data}))

        .catch( (erro) => console.log(erro) )
    }

    /* BUSCAR CONSULTAS  */

    cadastrarConsulta = (event) => {
        event.preventDefault()

        fetch('http://localhost:5000/api/consulta', {
            method : 'POST',

            body : JSON.stringify(
            {
                medicoNome : this.state.medicoNome, pacienteNome : this.state.pacienteNome, horarioConsulta : this.state.horarioConsulta, statusConsulta : this.state.statusConsulta, descricaoConsulta : this.state.descricaoDaConsulta
            }),

            headers : {
                'Authorization' : 'Bearer ' + localStorage.getItem('usuario-login') 
            }
        }) 
        
        .then(console.log('Consulta cadastrada com sucessso'))

        .then(this.buscarConsultas)

        .catch(erro => console.log(erro))

        .then(this.limparCampos)
    }

    atualizarEstadoMedico = async(event) => {
        await this.setState({medicoNome : event.target.value})

        console.log(this.state.medicoNome)

        event.preventDefault();
    }

    atualizarEstadoPaciente = async(event) => {
        await this.setState({pacienteNome : event.target.value})

        console.log(this.state.pacienteNome)

        event.preventDefault()
    }

    atualizarEstadoHorario = async(event) => {
        await this.setState({horarioConsulta : event.target.value})

        console.log(this.state.horarioConsulta)

        event.preventDefault()
    }
    

    atualizarEstadoStatusConsulta = async(event) => { /* STATUSCONSULTA É UM MÉTODO DIFERENTE DE SE FAZER */
        await this.setState({statusConsulta : event.target.value})

        console.log(this.state.statusConsulta)

        event.preventDefault()
    }

    atualizarEstadoDescricao = async(event) => {
        await this.setState({descricaoConsulta : event.target.value})

        console.log(this.state.descricaoConsulta)

        event.preventDefault()
    }

    componentDidMount(){
        this.buscarConsultas()
    }


    render(){
        return(
            <div>
                <Header />
                <Link to="/"><h3 className="logout">Sair</h3></Link>
                <section className="section-cadastrar-consulta">
                    <h1>Cadastrar consultas</h1>
                    <div className="cadastrar-consulta">
                        <form className="cadastrarConsulta">
                            <h2>Nome do médico</h2>
                            <input value={this.state.nomeMedico} 
                                onChange={this.atualizarEstadoMedico} 
                                required 
                                type="text" placeholder="Nome do médico" 
                            />

                            <h2>Nome do paciente:</h2>
                            <input value={this.state.nomePaciente} type="text" placeholder="Nome do Paciente" />

                            <h2>Horário da consulta:</h2>
                            <input type="text" placeholder="Horário da consulta" />

                            <h2>Data da consulta:</h2>
                            <input type="date" placeholder="Data da consulta" />

                            <h2>Status da Consulta:</h2>
                            <select>
                                <option>Agendada</option>
                                <option>Confirmada</option>
                                <option>Cancelada</option>
                            </select>

                            <h2>Descrição da consulta:</h2>
                            <input type="text" placeholder="Descrição da consulta" />

                            <button type="submit">Cadastrar</button>
                        </form>
                    </div>
                </section>
                <section className="section-consultas">
                <h1>Consultas</h1>
                <table>
                    <thead>
                        <tr>
                            <th>Médico</th>
                            <th>Pacientes</th>
                            <th>Especialidade</th>
                            <th>Data</th>
                            <th>Horário</th>
                            <th>Status</th>
                            <th>Descrição</th>
                        </tr>
                    </thead>
                    <tbody>
                         {
                            this.state.listaConsultas.map((consulta) => {
                                return(
                                    <tr key={consulta.idConsulta}>
                                        <td>{consulta.idMedicoNavigation.nomeMedico}</td>
                                        <td>{consulta.idPacienteNavigation.nomePaciente}</td>
                                        <td>{consulta.idMedicoNavigation.idEspecialidadeNavigation.descricaoEspec}</td>
                                        <td>{consulta.dataConsulta}</td>
                                        <td>{consulta.idConsulta.hroConsulta}</td>
                                        <td>{consulta.idStatusConsultaNavigation.statusConsulta}</td>
                                        <td>{consulta.descricaoConsulta}</td>
                                    </tr>
                                )}
                            )
                        }     
                                     
                    </tbody>    
                </table>
                </section>                
            </div>
        )
    }
}    

export default Admin;
