import {Component} from 'react'
import axios from 'axios';
import {Link, Switch} from 'react-router-dom';
import { parseJwt } from '../../services/auth';

import Header from '../../Components/header/header'

import '../../App.css';
import { render } from '@testing-library/react';



class Consultas extends Component(){
    constructor(props){
        super(props);
        this.state = {
            listaConsultas : [],
            descricao : '',
            atualizando : '',
            idDescricaoAlterado : 0
        }
    }

    getConsulta = () => {
        axios.get('http://localhost:5000/api/consulta/minhas-consultas/ ' + parseJwt().jti, {
            headers : {
                'Authorization' : 'Bearer ' + localStorage.getItem('usuario-login')
            }
        })

        .then( data => this.setState({listaConsultas : data}))

        .catch( (erro) => console.log(erro))
    }

    atualizaDescricao = async(evento) => {
        await this.setState({descricao : evento.target.value})

        console.log(this.state.descricao)

        evento.preventDefault();
        
    }

    buscarDescricaoPorId = async(Desejo) => {
        await this.setState({
            idDescricaoAlterado : Desejo
        })
    }
  
render(){
    return(
        <div>
            <Header />
            <Link to="/"><h3 className="logout">Sair</h3></Link>
               
            <section className="section-consultas">
                <h1>Consultas</h1>
                <table>
                    <thead>
                        <tr>
                            <th>Médico</th>
                            <th>Pacientes</th>
                            <th>Especialidade</th>
                            <th>Data</th>
                            <th>Horário</th>
                            <th>Status</th>
                            <th>Descrição</th>
                            <th>Editar</th>
                        </tr>
                    </thead>
                    <tbody>
                        {/* {
                            listaConsultas.map((consulta) => {
                                return(
                                    <tr key={consulta.idConsulta}>
                                        <td>{consulta.idMedicoNavigation.nomeMedico}</td>
                                        <td>{consulta.idPacienteNavigation.nomePaciente}</td>
                                        <td>{consulta.idMedicoNavigation.idEspecialidadeNavigation.descricaoEspec}</td>
                                        <td>{consulta.dataConsulta}</td>
                                        <td>{consulta.hroConsulta}</td>
                                        <td>{consulta.idStatusConsultaNavigation.statusConsulta}</td>
                                        <td>{consulta.descricaoConsulta}</td>
                                        <td>
                                            <form className="for" onSubmit="">
                                                <button className="Editar"type="submit">Editar</button>
                                            </form>
                                        </td>
                                    </tr>
                                )
                            })
                        } */}
                    </tbody>
                </table>
            </section>
            <section className="section-editar">
                <h1>Editar Descrição</h1>
                <div className="editar">
                        <form className="form-alterar" onSubmit="">
                            <input type="text" placeholder="Altere a descrição da consulta desejada"/>
                            <button className="btn-alterar" type="submit">Alterar</button>
                        </form>
                </div>
            </section>
        </div>
    )

}
}

export default Consultas;
